statements.insert:
select SEQ_SM_SITE_INFO.nextval as IntId from dual
insert into SM_Site_Info ( I_INTID, C_SITEID, C_SITENAME, C_SITETYPE, C_SITELOCATION, C_SITEIP1, C_SITEIP2, I_SITELEVEL, I_ISAUTOWEIGH, C_CREATEUSER, C_CREATETIME
)values ( #IntId#, #siteId#, #siteName#, #siteType.IntValue#, #siteLocation#, #siteIp1#, #siteIp2#, #siteLevel#, #IsAutoWeigh.EntityValue#, #createUser#, to_char(sysdate,'yyyyMMddhh24miss')
)
